    });

    function updateLiveAttackTable(msg) {
        const sensor = msg.sensor || 'unknown';
        const data = msg.data || {};
        const geo = msg.geo || {};
        const tsStr = data.timestamp;
        if (!tsStr || sensor === "all") return;

        const logTime = new Date(tsStr);
        const now = new Date();
        if ((now - logTime) / (1000 * 60) > 60 * 24) return;

        const ip = data.src_ip || data.remote_ip || 'N/A';
        const country = geo.country || 'Unknown';
        const encodedCountry = encodeURIComponent(country);
        const countryFlag = `<img src="/assets/img/flags/${encodedCountry}.svg" alt="${country}" title="${country}" width="24" height="18" />`;
        const city = geo.city || '-';
        const lat = geo.latitude ?? '-';
        const lon = geo.longitude ?? '-';

        const newRow = `
            <tr>
                <td>${sensor}</td>
                <td>${ip}</td>
                <td>${countryFlag} ${country}</td>
                <td>${city}</td>
                <td>${lat}</td>
                <td>${lon}</td>
                <td>${tsStr}</td>
            </tr>
        `;

        const tbodyLive = $('#liveAttackTable tbody');
        tbodyLive.prepend(newRow);
        if (tbodyLive.children().length > 10) {
            tbodyLive.children().last().remove();
        }
    }

    function loadTopProtocolPort() {
        $.get("/top-protocol-port", function (res) {
            const data = res.data || [];
            const tbody1 = $('#top10IpAttacker tbody');
            const tbody2 = $('#websockettopProtocol tbody');
            tbody1.empty(); tbody2.empty();
            data.slice(0, 10).forEach(([key, count], i) => {
                const [protocol, port] = key.split(':');
                const row = `<tr><td>${i+1}</td><td>${protocol}</td><td>${port}</td><td>${count}</td></tr>`;
                tbody1.append(row);
                tbody2.append(row);
            });
        });
    }

    function loadTopSourceIp() {
        $.get("/top-source-ip", function (res) {
            const data = res.data || [];
            const tbody1 = $('#attackSourceIP tbody');
            const tbody2 = $('#websocketattackSourceIP tbody');
            tbody1.empty(); tbody2.empty();
            data.slice(0, 10).forEach(([ip, count], i) => {
                const row = `<tr><td>${i+1}</td><td>${ip}</td><td>${count}</td></tr>`;
                tbody1.append(row);
                tbody2.append(row);
            });
        });
    }

    function loadTopSensorTable() {
        $.get("/top-sensor-count", function (res) {
            const data = res.data || [];
            const tbody1 = $('#attackSensor tbody');
            const tbody2 = $('#websocketattackSensor tbody');
            tbody1.empty(); tbody2.empty();
            data.slice(0, 10).forEach(([sensor, count], i) => {
                const row = `<tr><td>${i+1}</td><td>${sensor}</td><td>${Number(count).toLocaleString()}</td></tr>`;
                tbody1.append(row);
                tbody2.append(row);
            });
            renderSensorAverageTable(data);
        });
    }

    function loadAttackInformation() {
        $.get("/top-sensor-count", function (res) {
            const data = res.data || [];
            const showAverage = $('#showAverage').is(':checked');
            const showTotal = $('#showTotal').is(':checked');
            let total = 0, html = "";

            data.forEach(([sensor, count]) => {
                total += Number(count);
                html += `<div class="d-flex justify-content-between"><span>${sensor}</span><span>${Number(count).toLocaleString()}</span></div>`;
            });

            $('#websocketsensorAttackList').html(html);
            $('#websocketTotalAttack').text(showTotal ? total.toLocaleString() : "Hidden");
            $('#websockettotalAttackAverage').text(showAverage && data.length > 0 ? `Avg: ${Math.round(total / data.length).toLocaleString()}` : "");
        });
    }

    function renderSensorAverageTable(data) {
        const container = $('#totalAttackAverage');
        const showAverage = $('#showAverage').is(':checked');
        if (!showAverage) return container.empty();

        container.empty();
        const ul = $('<ul class="list-unstyled mb-0" style="text-align: right"></ul>');
        data.forEach(([sensor, count]) => {
            ul.append(`<li style="display: flex; justify-content: space-between;"><span>${sensor}</span><span>${Math.round(count)}</span></li>`);
        });
        container.append(ul);
    }

    $('#showAverage').on('change', function () {
        if (this.checked) $('#showTotal').prop('checked', false);
        loadAttackInformation();
    });

    $('#showTotal').on('change', function () {
        if (this.checked) $('#showAverage').prop('checked', false);
        loadAttackInformation();
    });

    setInterval(loadTopProtocolPort, 5 * 60 * 1000);
    setInterval(loadTopSourceIp, 5 * 60 * 1000);
    setInterval(loadTopSensorTable, 5 * 60 * 1000);
    setInterval(loadAttackInformation, 5 * 60 * 1000);
});
</script>
@endguest
@endpush